import React, { useState, useEffect } from 'react';
import { createPaciente, updatePaciente } from '../../api/pacientesApi';
import styles from './pacienteForm.module.css';

export default function PacientesForm({ onClose, onCreated, initialData, onUpdated }) {
  const [form, setForm] = useState({
    dni_paciente: '',
    nombre_paciente: '',
    apellido_paciente: '',
    fecha_nacimiento: '',
    domicilio: '',
    localidad: '',
    telefono: '',
    correo: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  function handleChange(e) {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  }

  useEffect(() => {
    if (initialData) {
      setForm({
        dni_paciente: initialData.dni_paciente || '',
        nombre_paciente: initialData.nombre_paciente || '',
        apellido_paciente: initialData.apellido_paciente || '',
        fecha_nacimiento: initialData.fecha_nacimiento || '',
        domicilio: initialData.domicilio || '',
        localidad: initialData.localidad || '',
        telefono: initialData.telefono || '',
        correo: initialData.correo || ''
      });
    }
  }, [initialData]);

  async function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    setError(null);
    try {
      if (initialData?.id_paciente) {
        const resp = await updatePaciente(initialData.id_paciente, form);
        if (resp?.success) {
          onUpdated?.(resp.data);
          onClose?.();
        } else {
          setError(resp?.errors || resp?.error || 'Error al actualizar paciente');
        }
      } else {
        const resp = await createPaciente(form);
        if (resp?.success) {
          onCreated?.(resp.data);
          onClose?.();
        } else {
          setError(resp?.errors || resp?.error || 'Error al crear paciente');
        }
      }
    } catch (e) {
      setError(e.message || String(e));
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className={styles.overlay}>
      <div className={styles.formCard}>
        <button className={styles.closeBtn} onClick={onClose}>Cerrar</button>

        <h2 className={styles.title}>
          {initialData ? 'Editar paciente' : 'Nuevo paciente'}
        </h2>

        {error && <div className={styles.error}>{JSON.stringify(error)}</div>}

        <form className={styles.form} onSubmit={handleSubmit}>
          {[
            { label: 'DNI', name: 'dni_paciente' },
            { label: 'Nombre', name: 'nombre_paciente' },
            { label: 'Apellido', name: 'apellido_paciente' },
            { label: 'Fecha de nacimiento', name: 'fecha_nacimiento', type: 'date' },
            { label: 'Teléfono', name: 'telefono' },
            { label: 'Correo', name: 'correo' },
            { label: 'Domicilio', name: 'domicilio' },
            { label: 'Localidad', name: 'localidad' }
          ].map(({ label, name, type = 'text' }) => (
            <div className={styles.inputGroup} key={name}>
              <label>{label}</label>
              <input
                type={type}
                name={name}
                value={form[name]}
                onChange={handleChange}
              />
            </div>
          ))}

          <div className={styles.buttonGroup}>
            <button className={styles.btnSubmit} type="submit" disabled={loading}>
              {loading ? 'Guardando...' : initialData ? 'Actualizar' : 'Crear'}
            </button>
            <button className={styles.btnCancel} type="button" onClick={onClose}>
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
