import React, { useState } from 'react';
import styles from './pacienteCard.module.css';

export default function PacienteCard({
  paciente,
  onClose,
  onEditar,
  onEliminar,
  onAsignarObra,
  onAgregarFicha
}) {
  const [closing, setClosing] = useState(false);

  if (!paciente) return null;

  const handleEliminar = () => {
    if (window.confirm(`¿Seguro que querés dar de baja a ${paciente.nombre_paciente}?`)) {
      onEliminar(paciente.id_paciente);
    }
  };

  const handleClose = () => {
    setClosing(true);
    setTimeout(() => {
      onClose && onClose();
    }, 400); // coincide con la transición CSS
  };

  return (
    <div className={`${styles.pacienteOverlay} ${closing ? styles.closing : ''}`}>
      <div className={`${styles.pacienteCard} ${closing ? styles.closing : ''}`}>
        <button className={styles.pacienteCloseBtn} onClick={handleClose}>✕</button>

        <h2 className={styles.pacienteTitle}>
          {paciente.nombre_paciente} {paciente.apellido_paciente}
        </h2>

        <div className={styles.pacienteInfo}>
          <p><strong>DNI:</strong> {paciente.dni_paciente}</p>
          <p><strong>Fecha Nac.:</strong> {paciente.fecha_nacimiento}</p>
          <p><strong>Teléfono:</strong> {paciente.telefono}</p>
          <p><strong>Correo:</strong> {paciente.correo}</p>
        </div>

        <h4 className={styles.pacienteSubtitle}>Obras Sociales</h4>
        <div className={styles.pacienteObras}>
          {paciente.obras_sociales?.length ? (
            <ul>
              {paciente.obras_sociales.map(os => (
                <li key={os.id_paciente_os}>
                  {os.obra_social_nombre} — <span>{os.credencial_paciente || "sin credencial"}</span>
                </li>
              ))}
            </ul>
          ) : (
            <p>Sin obras sociales registradas</p>
          )}
        </div>

        <div className={styles.pacienteButtons}>
          <button className={`${styles.btn} ${styles.edit}`} onClick={() => onEditar(paciente.id_paciente)}>Editar</button>
          <button className={`${styles.btn} ${styles.delete}`} onClick={handleEliminar}>Dar de baja</button>
          <button className={`${styles.btn} ${styles.obra}`} onClick={() => onAsignarObra(paciente.id_paciente)}>Asignar Obra Social</button>
          <button className={`${styles.btn} ${styles.ficha}`} onClick={() => onAgregarFicha(paciente.id_paciente)}>Agregar Ficha Patológica</button>
        </div>
      </div>
    </div>
  );
}
