import React, { useState } from 'react';
import './pacienteCard.css';

export default function PacienteCard({
  paciente,
  onClose,
  onEditar,
  onEliminar,
  onAsignarObra,
  onAgregarFicha
}) {
  const [closing, setClosing] = useState(false);

  if (!paciente) return null;

  const handleEliminar = () => {
    if (window.confirm(`¿Seguro que querés dar de baja a ${paciente.nombre_paciente}?`)) {
      onEliminar(paciente.id_paciente);
    }
  };

  const handleClose = () => {
    setClosing(true);
    setTimeout(() => {
      onClose && onClose();
    }, 300);
  };

  return (
    <div className={`paciente-overlay ${closing ? 'closing' : ''}`}>
      <div className={`paciente-card ${closing ? 'closing' : ''}`}>
        <button className="paciente-close-btn" onClick={handleClose}>✕ Cerrar</button>

        <h2 className="paciente-title">
          {paciente.nombre_paciente} {paciente.apellido_paciente}
        </h2>

        <div className="paciente-info">
          <p><strong>DNI:</strong> {paciente.dni_paciente}</p>
          <p><strong>Fecha Nac.:</strong> {paciente.fecha_nacimiento}</p>
          <p><strong>Teléfono:</strong> {paciente.telefono}</p>
          <p><strong>Correo:</strong> {paciente.correo}</p>
        </div>

        <h4 className="paciente-subtitle">Obras Sociales</h4>
        <div className="paciente-obras">
          {paciente.obras_sociales && paciente.obras_sociales.length ? (
            <ul>
              {paciente.obras_sociales.map(os => (
                <li key={os.id_paciente_os}>
                  {os.obra_social_nombre} — <span>{os.credencial_paciente || "sin credencial"}</span>
                </li>
              ))}
            </ul>
          ) : (
            <p>Sin obras sociales registradas</p>
          )}
        </div>

        <div className="paciente-buttons">
          <button className="btn edit" onClick={() => onEditar(paciente.id_paciente)}>Editar</button>
          <button className="btn delete" onClick={handleEliminar}>Dar de baja</button>
          <button className="btn obra" onClick={() => onAsignarObra(paciente.id_paciente)}>Asignar Obra Social</button>
          <button className="btn ficha" onClick={() => onAgregarFicha(paciente.id_paciente)}>Agregar Ficha Patológica</button>
        </div>
      </div>
    </div>
  );
}
